            Yes
          </Button>
        </Modal.Footer>
      </Modal>


      <Modal
        size="lg"
        show={editModal}
        onHide={handleClose}
        aria-labelledby="example-modal-sizes-title-lg"
      >
        <Modal.Header closeButton>
          <Modal.Title id="example-modal-sizes-title-lg">
            Edit VilakshanMap
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="container-fluid">
            <div className="row clearfix">
              <div className="col-lg-12 col-md-12 col-sm-12">
                <div className="card">
                  <div className="body">
                    {/* <form> */}
                    <h2 className="card-inside-title">
                      <strong>Vilakshan and Associated Parameters </strong>
                    </h2>
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Vilakshan and Associated Parameters"
                      name="vilakParaE"
                      onChange={changeInpts}
                      value={vilakParaE}
                    />
                    {/* <input type="text" onChange={changeInpt} value={vilakParaE}/> */}
                    <h2 className="card-inside-title">
                      <strong>
                        FYE {Fdate}-{date + 1}
                      </strong>
                    </h2>
                    <input
                      type="text"
                      className="form-control"
                      placeholder={`FYE ${Fdate}-${date + 1}`}
                      name="fyE1"
                      onChange={(event) => {
                        setfyE1(event.target.value);
                      }}
                      value={fyE1}
                    />
                    <h2 className="card-inside-title">
                      <strong>
                        FYE {Fdate + 1}-{date + 2}
                      </strong>
                    </h2>
                    <input
                      type="text"
                      className="form-control"
                      placeholder={`FYE ${Fdate + 1}-${date + 2}`}
                      name="fyE2"
                      onChange={(event) => {
                        setfyE2(event.target.value);
                      }}
                      value={fyE2}
                    />
                    <h2 className="card-inside-title">
                      <strong>
                        FYE {Fdate + 2}-{date + 3}
                      </strong>
                    </h2>
                    <input
                      type="text"
                      className="form-control"
                      placeholder={`FYE ${Fdate + 2}-${date + 3}`}
                      name="fyE3"
                      onChange={(event) => {
                        setfyE3(event.target.value);
                      }}
                      value={fyE3}
                    />
                    <h2 className="card-inside-title">
                      <strong>
                        FYE {Fdate + 3}-{date + 4}
                      </strong>
                    </h2>
                    <input
                      type="text"
                      className="form-control"
                      placeholder={fyE4}
                      name="fyE4"
                      onChange={(event) => {
                        setfyE4(event.target.value);
                      }}
                      value={fyE4}
                    />

                    <h2 className="card-inside-title">
                      <strong>
                        FYE {Fdate + 4}-{date + 5}
                      </strong>
                    </h2>
                    <input
                      type="text"
                      className="form-control"
                      name="setfyE5"
                      placeholder={`FYE ${Fdate + 4}-${date + 5}`}
                      onChange={(event) => {
                        setfyE5(event.target.value);
                      }}
                      value={fyE5}
                    />
                    <div style={{ marginTop: 20 }}></div>
                    <button
                      type="button"
                      onClick={() => OnSubmitUpdate(editId)}
                      class="btn savebtn btn-square waves-effect"
                    >
                      Update
                    </button>
                    {/* </form> */}
                  </div>
                  <div style={{ marginTop: 30 }}></div>
                </div>
              </div>
            </div>
          </div>
        </Modal.Body>
      </Modal>

    </>
  );
}
