                          />
                        </div>
                      </div>
                    </div>

                    <button
                      type="submit"
                      class="btn btn-success  waves-effect"
                      onClick={() => OnSubmitUpdate(editId)}
                    >
                      Update
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </Modal.Body>
      </Modal>
    </>
  );
};
export default Table;
