    // alert(rebirthDataa);
    if (rebirthDataa.length === 0) {
      var myHeaders = new Headers();
      myHeaders.append("Content-Type", "application/json");
      // inputList.map((item,key)=>{
      var raw = JSON.stringify({
        Search: inputList,
        Manan: frameManan,
        Sadhana: frameSadhna,
        Spiritual: spritualFoundation,
        email_id: s_id,
        created_by: s_id,
        updated_by: s_id,
      });
      var requestOptions = {
        method: "POST",
        headers: myHeaders,
        body: raw,
        redirect: "follow",
      };
      fetch(`http://localhost:9002/masters/rebirth/`, requestOptions)
        .then((response) => response.json())
        .then((resData) => {
          console.log(resData);
