            console.log("Values Submitted Succesfully");
            setSmShow(true);
            setTimeout(() => {
              setSmShow(false);
              props.Onvalidate(true);
            }, 1000)
          }
          GetallRecords();
        })
        .catch((error) => console.log("error", error));
